import { useEffect, useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss"
import { featuredPortfolio, webPortfolio, mobilePortfolio, dataPortfolio } from "../../data";

export default function Portfolio() {
        const [selected, setSelected] = useState("featured");
        const [data, setData] = useState([]);
    const list = [
        {
            id: "featured",
            title: "Featured",
        },
        {
            id: "web",
            title: "Web App",
        },
        {
            id: "mobile",
            title: "Mobile App",
        },
        {
            id: "data",
            title: "Data",
        },

    ];

    useEffect(()=>{
        switch(selected){
            case "featured":
            setData(featuredPortfolio);
            break;
            case "web":
            setData(webPortfolio);
            break;  
            case "mobile":
            setData(mobilePortfolio);
            break;  
            case "data":
            setData(dataPortfolio);
            break;
            default:
                setData(featuredPortfolio);      
        }

    },[selected])


    return (
        <div className = "portfolio" id="portfolio">
            <h1>Portfolio</h1>
            <ul>
                {list.map(item =>( 
                    <PortfolioList 
                    title={item.title} 
                    active={selected === item.id} 
                    setSelected={setSelected}
                    id={item.id}
                    />))

                }
            </ul>
            <div className="container">
                <div className="item">
                    <img src="asset/design.png" alt="" />
                    <h3>Web App</h3>
                </div>
                <div className="item">
                    <img src="asset/design.png" alt="" />
                    <h3>Web App</h3>
                </div>
                <div className="item">
                    <img src="asset/design.png" alt="" />
                    <h3>Web App</h3>
                </div>
            </div>
        </div>
    )
}
